mapper.select:
SELECT RNUM , TOTALCOUNT , NO , CONTENT , CREATED_DATE , MEMBER_NO , LOGINID , I_POST_NO , PATH , NAME , EXTENSION , LIKED , LIKECOUNT , LIKEMEMBER , MIPATH , MINAME , MIEXTENSION FROM (SELECT ROWNUM AS RNUM , T.* FROM (SELECT (SELECT COUNT(P.NO) FROM POST P, POST_IMAGE I, MEMBER M, MEMBER_IMAGE MI WHERE P.NO = I.POST_NO AND P.MEMBER_NO = M.NO AND P.MEMBER_NO = MI.MEMBER_NO(+) AND M.DELETED = 'N'
AND P.NO IN (SELECT POST_NO FROM (SELECT H.POST_NO FROM HASHTAG H WHERE H.NAME = #{hashtag}))
) AS TOTALCOUNT , P.NO AS NO , P.CONTENT AS CONTENT , P.CREATED_DATE AS CREATED_DATE , I.POST_NO AS I_POST_NO , I.PATH AS PATH , I.NAME AS NAME , I.EXTENSION AS EXTENSION , M.NO AS MEMBER_NO , M.LOGINID AS LOGINID , MI.PATH AS MIPATH , MI.NAME AS MINAME , MI.EXTENSION AS MIEXTENSION , NVL((SELECT NO FROM POST_LIKE WHERE POST_NO = P.NO AND MEMBER_NO = #{memberNo}), 0) AS LIKED , NVL((SELECT COUNT(MEMBER_NO) FROM POST_LIKE WHERE POST_NO = P.NO), 0) AS LIKECOUNT , (SELECT LOGINID FROM MEMBER WHERE NO = (SELECT MEMBER_NO FROM POST_LIKE WHERE NO = (SELECT MAX(NO) AS MAXNO FROM POST_LIKE WHERE POST_NO = P.NO))) AS LIKEMEMBER FROM POST P, POST_IMAGE I, MEMBER M, MEMBER_IMAGE MI WHERE P.NO = I.POST_NO AND P.MEMBER_NO = M.NO AND P.MEMBER_NO = MI.MEMBER_NO(+) AND M.DELETED = 'N'
AND P.NO IN (SELECT POST_NO FROM (SELECT H.POST_NO FROM HASHTAG H WHERE H.NAME = #{hashtag}))
ORDER BY P.NO DESC) T
WHERE ROWNUM < #{row}+3 ) WHERE RNUM >= #{row}
